(function ($) {
// Função para fazer o scroll suave até a section

var $doc = $('html, body');
$('.scroll').click(function () {
    $doc.animate({
    scrollTop: $($.attr(this, 'href')).offset().top - 60
    }, 500);
    return false;
});

"use strict";

// Função para fechar o menu no mobile quando algum link for clicado
$('.nav-link').on('click',function() {
    $('.navbar-collapse').collapse('hide');
});

})(jQuery);